import streamlit
import streamlit as st
import pickle
import numpy as np
import sklearn



pipe = pickle.load(open('pipe.pkl','rb'))
df = pickle.load(open('df.pkl','rb'))





st.title(" Pizza  Price  Predictor")

st.sidebar.image("https://c.ndtvimg.com/2020-07/ds980vng_pizza_625x300_07_July_20.jpg")

# company

company = st.sidebar.selectbox('Select The Brand',df['company'].unique())

#diameter

diameter = st.sidebar.selectbox('Select The Diameter(in Meter)',[10,12,15,18,20,22,25,28,30])

# Pizza topping
topping= st.sidebar.selectbox("Select The Topping", df['topping'].unique())

#variant
variant= st.sidebar.selectbox("Select The category ", df['variant'].unique())


#size
size= st.sidebar.selectbox("Select The Size ", df['size'].unique())

#extra_sauce

extra_sauce= st.sidebar.selectbox("Want extra_sauce ",['No','Yes'])

#extra_cheese

extra_cheese= st.sidebar.selectbox("Want extra_cheese ",['No','Yes'])








if st.button('Predict Price'):

    if topping == "chicken":

        st.image("https://i.pinimg.com/originals/36/19/73/361973f2eb851be261cceaa866c2ea39.jpg")

    if topping == "papperoni":
        st.image("https://cdn.tasteatlas.com/images/dishes/b05a0af72ad845f3a6abe16143d7853a.jpg?w=600&h=450")

    if topping == "mushrooms":
        st.image("data:image/jpeg;base64,")

    if topping == "smoked beef":
        st.image("https://mamajoun.com/wp-content/uploads/2017/06/Montreal-style-smoked-meat-and-pepperoni-pizza-1024x768.jpg")

    if topping == "mozzarella":
        st.image("https://media.istockphoto.com/photos/pizza-margarita-with-mozzarella-cheese-basil-and-tomato-template-for-picture-id905492462")

    if topping == "black papper":
        st.image("https://i.ytimg.com/vi/ew3gqElR414/maxresdefault.jpg")

    if topping == "tuna":
        st.image("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQL0FcIcFQsb7LXJ1wHYYbNBzry3ByY1u4DQQ&usqp=CAU")

    if topping == "meat":
        st.image("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQhplq3LIINhrQJVywDyK3JeAS0d5Poffcayg&usqp=CAU")

    if topping == "sausage":
        st.image("data:image/jpeg;base64,")

    if topping == "onion":
        st.image("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRkRUkeh75FU6DgZgX8oPrf7PYCuAN5nC3UoA&usqp=CAU")

    if topping == "vegetables":
        st.image("data:image/jpeg;base64,")


    # query
    #ppi = None
    if extra_sauce == 'Yes':
        extra_sauce = 1
    else:
        extra_sauce = 0

    if extra_cheese == 'Yes':
        extra_cheese = 1
    else:
        extra_cheese = 0

    query = np.array([company, diameter,topping,variant, size, extra_sauce, extra_cheese])
    query = query.reshape(1, 7)

    #st.title("The predicted price of this Pizza is " + str(float(np.exp(pipe.predict(query)[0]))))
    st.title("The predicted price of this Pizza is ₹ " + str(int(float(pipe.predict(query)[0]))))
